import React from "react";
import "./MenuToggle.css";

const MenuToggle = props => {
  const cls = ["MenuToggle", "fa"];
  if (props.isOpen) {
    cls.push("fa-times");
    cls.push("MenuToggle__open");
  } else {
    cls.push("fa-bars");
  }
  return (
    <div className="Navigation">
      <div
        className="Navigation__button Navigation__button--toggle"
        onClick={props.onToggle}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>
    </div>
  );
};
export default MenuToggle;
