import {
  CREATE_QUIZ_QUESTION,
  RESET_QUIZ_CREATION
} from "../actions/actionTypes";

const initialState = {
  quizItem: {
    quiz: [],
    author: "nancy",
    title: "Кто ты из гарри поттера"
  }
};

export default function createReducer(state = initialState, action) {
  switch (action.type) {
    case CREATE_QUIZ_QUESTION:
      const quizItem = {
        quiz: [...state.quizItem.quiz]
      };
      return {
        ...state,
        quizItem: [...state.quizItem.quiz, action.item]
      };
    case RESET_QUIZ_CREATION:
      return {
        ...state,
        quizItem: {
          quiz: [],
          author: "",
          title: ""
        }
      };
    default:
      return state;
  }
}
